# Google Parser Image
# Version 0.2

import json
from urllib.request import urlretrieve
from selenium import webdriver
import os

search_term = 'Участники ВОВ'  # Запрос для поиска
url = "https://www.google.co.in/search?q=" + search_term + "&source=lnms&tbm=isch"
browser = webdriver.Chrome()  # какой драйвер используем
browser.get(url)
img_count = 0
extensions = {"jpg", "jpeg", "png", "gif"}  # форматы изображений для поиска

if not os.path.exists(search_term):
    os.mkdir(search_term)

for _ in range(3000):
    browser.execute_script("window.scrollBy(0,20000)")

html = browser.page_source.split('["')
data_google = []
for i in html:
    # print(i)
    if i.startswith('http') and i.split('"')[0].split('.')[-1] in extensions:
        data_google.append([i.split('"')[0], i.split('"')[10], i.split('"')[12]])

print(len(data_google))  # кол-во картинок получено для одного запроса
